export function changeBackground() {
    const header = document.querySelector('.header');
    const images = [
        "./images/1.jpg",
        "./images/2.jpg",
        "./images/3.jpg",
        "./images/4.jpg",
        "./images/5.jpg"
    ];

    let currentIndex = 0;

    function changeBackgroundInternal() {
        header.style.backgroundImage = `url(${images[currentIndex]})`;
        currentIndex = (currentIndex + 1) % images.length;
        setTimeout(changeBackgroundInternal, 10000);
    }

    changeBackgroundInternal();
}






   